import React, { useState } from 'react';
import moment from 'moment'; // 날짜 포맷을 위해 moment.js 사용

export function MainPage() {
  // 예시 데이터
  const exampleData = new Array(50).fill(null).map((_, index) => ({
    id: index + 1,
    writer: `작성자 ${index + 1}`,
    title: `제목 ${index + 1}`,
    createdAt: moment().subtract(index, 'days').format('YYYY-MM-DD HH:mm:ss'),
  }));

  const [boards, setBoards] = useState(exampleData);
  const [viewCount, setViewCount] = useState(10); // 페이지 당 보여줄 게시글 수
  const [currentPage, setCurrentPage] = useState(1); // 현재 페이지 번호

  // 페이지 당 게시글 수 변경
  const handleViewCountChange = (e) => {
    setViewCount(Number(e.target.value));
    setCurrentPage(1); // 보여줄 항목 수 변경 시 첫 페이지로 리셋
  };

  // 페이지네이션을 위한 게시글 분할
  const indexOfLastBoard = currentPage * viewCount;
  const indexOfFirstBoard = indexOfLastBoard - viewCount;
  const currentBoards = boards.slice(indexOfFirstBoard, indexOfLastBoard);

  // 페이지 번호 클릭 이벤트
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // 페이지 번호를 위한 컴포넌트
  const PageNumbers = () => {
    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(boards.length / viewCount); i++) {
      pageNumbers.push(i);
    }

    return (
      <nav>
        <ul className='pagination'>
          {pageNumbers.map(number => (
            <li key={number} className='page-item'>
              <a onClick={() => paginate(number)} href='!#' className='page-link'>
                {number}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    );
  };

  return (
    <div>
      <h2>게시판</h2>
      <select value={viewCount} onChange={handleViewCountChange}>
        <option value="10">10개씩 보기</option>
        <option value="20">20개씩 보기</option>
        <option value="30">30개씩 보기</option>
      </select>
      <table>
        <thead>
          <tr>
            <th>작성자</th>
            <th>제목</th>
            <th>작성 일자</th>
          </tr>
        </thead>
        <tbody>
          {currentBoards.map((board) => (
            <tr key={board.id}>
              <td>{board.writer}</td>
              <td>{board.title}</td>
              <td>{board.createdAt}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <PageNumbers />
      <button>글 작성</button>
    </div>
  );
}
